import React, { useState } from 'react'

export default function Message() {
const [showBtn,setShowBtn]=useState(true);

function submit(event) {
  event.preventDefault();
    console.log(event.target[0].value);
}

  return (
    <div className='container'>
           <div className="row">
                <form onSubmit={submit}>
                    <label htmlFor="inpNumber">Number</label>
                    <input type="text" placeholder='+998912345678'
                     className='form-control' required id='inpNumber'/>
                     <label htmlFor="inpCode" >Code</label>
                    <input type="number" placeholder='enter the code here' 
                    className='form-control' required id='inpCode'/>
                    {
                      showBtn ? 
                      <button className='btn btn-success' onClick={sendCode}>send code</button>
                      :
                      <button className='btn btn-warning' onClick={Confirm}>Confirm</button>
                    }
                </form>
           </div>
    </div>
  )
}
