import React, { useState, useEffect } from 'react';
import { MapContainer, TileLayer, Marker, Popup, Polyline, useMapEvents, Tooltip } from 'react-leaflet';
import Leaf from 'leaflet';

const GPSMap2 = () => {
  const [position, setPosition] = useState([51.505, -0.09]); // Initial position
  const [trail, setTrail] = useState([]);
  const [angles, setAngles] = useState([]);

  const customIcon = new Leaf.Icon({
    // iconUrl:  "https://freepngimg.com/thumb/car/75694-bird's-eye-car-top-view,plan-view-icon.png",
    iconUrl:require('./car.png'),
    iconSize: [35, 35],
    iconAnchor: [12, 41],
    popupAnchor: [1, -34],
    tooltipAnchor: [16, -28],
  });
  
  // Function to update position randomly for demonstration
  const updatePosition = () => {
    const lat = position[0] + Math.random() * 0.01; // Adjust as needed
    const lng = position[1] + Math.random() * 0.01; // Adjust as needed
    setPosition([lat, lng]);
    setTrail((prevTrail) => [...prevTrail, [lat, lng]]);
  };

  // Update position periodically
  useEffect(() => {
    const interval = setInterval(updatePosition, 3000); // Adjust interval as needed
    return () => clearInterval(interval);
  }, []);

  // Calculate angle between three points
  const calculateAngle = (p1, p2, p3) => {
    const angle1 = Math.atan2(p2[1] - p1[1], p2[0] - p1[0]);
    const angle2 = Math.atan2(p3[1] - p2[1], p3[0] - p2[0]);
    let angle = (angle2 - angle1) * (180 / Math.PI);
    if (angle < 0) angle += 360;
    return angle.toFixed(2);
  };

  // Calculate angles between consecutive segments in the trail
  useEffect(() => {
    const newAngles = [];
    for (let i = 1; i < trail.length - 1; i++) {
      const angle = calculateAngle(trail[i - 1], trail[i], trail[i + 1]);
      newAngles.push(angle);
    }
    setAngles(newAngles);
  }, [trail]);

  // Marker that moves continuously
  const WalkingMarker = () => (
    <Marker position={position}>
      <Popup>You are here</Popup>
    </Marker>
  );

  return (
    <MapContainer center={position} zoom={13} style={{ height: '400px', width: '100%' }}>
      <TileLayer
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
      />
      <WalkingMarker />
      <Polyline pathOptions={{ color: 'blue' }} positions={trail} />
      {angles.map((angle, index) => (
        <Marker key={index} position={trail[index + 1]}>
          <Tooltip direction="bottom" offset={[0, 20]}>{angle}Â°</Tooltip>
        </Marker>
      ))}
    </MapContainer>
  );
};

export default GPSMap2;
