import React from "react";

export default function Test() {

  
let lagmon = [41.270506, 69.259749];
let bukagym = [41.271460, 69.261978];
let center = [48.8866527839977,2.34310679732974];
let count=0;

let arr = [
	{
		lat: 41.271535, 
		lon: 69.262049,
		name: 'Метка 1'
	},
	{
	lat: 41.270506,
	lon: 69.259749,
	name: 'Метка 2',
    },
	{
		lat: 41.268052, 
		lon: 69.258837,
		name: 'Метка 3',
	},
	{
		lat: 41.269218, 
		lon: 69.263208 ,
		name: 'Метка 4',
	},

];

function init() {

	let map = new ymaps.Map('map-test', {
		center: bukagym,
		zoom: 15
	});


	let placemark1 = new ymaps.Placemark(bukagym, 
		{
		balloonContent: 
		`
			<div class="balloon">
				<div class="balloon__address">
				  <h5> Ahmad Abdullayev </h5>
				</div>
				<div class="balloon__contacts">
					<a>Number: +998995955545</a>
				</div>
			</div>

		`
	}, 
	{
		iconLayout: 'default#image',
		iconImageHref: 'car.png',
		iconImageSize: [40, 40],
		iconImageOffset: [-19, -44]
	}
	);

  map.controls.remove('geolocationControl'); // удаляем геолокацию
  map.controls.remove('searchControl'); // удаляем поиск
  map.controls.remove('trafficControl'); // удаляем контроль трафика
  map.controls.remove('typeSelector'); // удаляем тип
  map.controls.remove('fullscreenControl'); // удаляем кнопку перехода в полноэкранный режим
  map.controls.remove('zoomControl'); // удаляем контрол зуммирования
  map.controls.remove('rulerControl'); // удаляем контрол правил


	map.geoObjects.add(placemark1);
	// placemark1.balloon.open();


	function changeLocationAndPlacemark(latitude, longitude, placemarkContent) {
		// Change map center
		// map.setCenter([latitude, longitude]);

		// Remove existing placemark
		map.geoObjects.removeAll();

		// Create a new placemark at the desired location
		placemark1 = new ymaps.Placemark([latitude, longitude], 	{
			balloonContent: 
			`
				<div class="balloon">
					<div class="balloon__address">
					  <h5> Ahmad Abdullayev </h5>
					</div>
					<div class="balloon__contacts">
						<a>Number: +998995955545</a>
					</div>
				</div>
	
			`
		}, 
			
		{
			iconLayout: 'default#image',
			iconImageHref: 'car.png',
			iconImageSize: [40, 40],
			iconImageOffset: [-19, -44]
		});

		// Add the new placemark to the map
		map.geoObjects.add(placemark1);
	}

	// Example usage: Change location and placemark after 5 seconds

	setInterval(function() {
			
		count = count < 4 ? count += 1 : 0 ; 	
		changeLocationAndPlacemark( arr[count].lat,arr[count].lon ); 
        console.log(count);
		console.log(arr[count].lat,arr[count].lon);
		
	}, 1000 ); 

}

ymap.ready(init);




    // map.behaviors.disable(['scrollZoom']); // отключаем скролл карты (опционально)
    // map.geoObjects.add(placemark);
	// let placemark = new ymaps.Placemark(center, {
	// 	balloonContentHeader: 'Хедер балуна',
	// 	balloonContentBody: 'Боди балуна',
	// 	balloonContentFooter: 'Подвал',
	// }, {
	// 	iconLayout: 'default#image',
	// 	iconImageHref: "car.png",
	// 	iconImageSize: [40, 40],
	// 	iconImageOffset: [-19, -44]
	// });




  return (
    <body>
      <div id="map-test" className="map"></div>
      <script src="https://api-maps.yandex.ru/2.1/?apikey=b8342b76-6034-4d0a-be8b-20e125a532b9&lang=ru_RU"></script>
      <script src="script.js"></script>
    </body>
  );
}
