import React, { useState, useEffect } from 'react';
import { MapContainer, TileLayer, Marker, Popup, useMapEvents } from 'react-leaflet';

const GPSMap = () => {
  const [position, setPosition] = useState([51.505, -0.09]); // Initial position

  // Function to update position randomly for demonstration
  const updatePosition = () => {
    const lat = position[0] + Math.random() * 0.01; // Adjust as needed
    const lng = position[1] + Math.random() * 0.01; // Adjust as needed
    setPosition([lat, lng]);
  };

  // Update position periodically
  useEffect(() => {
    const interval = setInterval(updatePosition, 3000); // Adjust interval as needed
    return () => clearInterval(interval);
  }, []);

  // Marker that moves continuously
  const WalkingMarker = () => (
    <Marker position={position}>
      <Popup>You are here</Popup>
    </Marker>
  );

  return (
    <MapContainer center={position} zoom={13} style={{ height: '700px', width: '100%' }}>
      <TileLayer
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
      />
      <WalkingMarker />
    </MapContainer>
  );
};

export default GPSMap;
