import React, { useEffect, useState } from 'react';

const Googole = () => {
  const [map, setMap] = useState(null);

  useEffect(() => {
    // Initialize the map
    const mapInstance = new DG.Map('map', {
      center: [55.751244, 37.618423], // Moscow coordinates
      zoom: 10 // Initial zoom level
    });

    // Add marker to the map
    const marker = DG.marker([55.751244, 37.618423]).addTo(mapInstance).bindPopup('Moscow');

    // Set map instance in state
    setMap(mapInstance);

    // Cleanup function
    return () => {
      // Remove marker when component unmounts
      if (mapInstance.hasLayer(marker)) {
        mapInstance.removeLayer(marker);
      }
    };
  }, []); // Empty dependency array ensures this effect runs only once after initial render

  useEffect(() => {
    if (map) {
      // Change placemark after 5 seconds
      const timeoutId = setTimeout(() => {
        const newCoordinates = [40.7128, -74.0060]; // New York coordinates
        map.panTo(newCoordinates);
        map.eachLayer(function (layer) {
          if (layer instanceof DG.Marker) {
            layer.setLatLng(newCoordinates);
          }
        });
      }, 5000);

      // Cleanup function
      return () => clearTimeout(timeoutId);
    }
  }, [map]); // Dependency on the map instance

  return (
    <div id="map" style={{ width: '100%', height: '400px' }}></div>
  );
};

export default Googole;
