import React, { useEffect, useState } from "react";
import DG from "2gis-maps";
import { WebSocket } from "ws"; // Import WebSocket from 'ws' package

export default function Test() {
  const [sock, setSock] = useState(null); // State for WebSocket connection
  const [map, setMap] = useState(null); // State for map instance
  const [marker, setMarker] = useState(null); // State for marker instance

  useEffect(() => {
    // Create a WebSocket connection
    const websocket = new WebSocket("ws://204.12.199.7:9999/ws");
    setSock(websocket);

    // Cleanup function to close the WebSocket connection
    return () => {
      websocket.close();
    };
  }, []);

  useEffect(() => {
    if (!sock) return;

    // WebSocket event listeners
    sock.onopen = function (event) {
      console.log("Connection successful");
      sock.send("get data");
    };

    sock.onmessage = function (event) {
      let jsonData = JSON.parse(event.data);
      let newLocation = {
        latitude: Number(jsonData[0].Latitude),
        longitude: Number(jsonData[0].Longitude)
      };

      // Update location state
      setLocation(newLocation);
      console.log(newLocation);

      // Update marker position if map and marker exist
      if (map && marker) {
        marker.setLatLng([newLocation.latitude, newLocation.longitude]);
      }
    };

    sock.onclose = function (event) {
      console.log(event.data);
      console.log("Connection closed");
    };

    // Initialize the map and marker
    const newMap = DG.map("map-container", {
      center: [location.latitude, location.longitude],
      zoom: 15
    });
    setMap(newMap);

    const icon = DG.icon({
      iconUrl:
        "https://freepngimg.com/thumb/car/75694-bird's-eye-car-top-view,plan-view-icon.png",
      iconSize: [35, 30]
    });

    // Create marker
    const newMarker = DG.marker([location.latitude, location.longitude], { icon: icon }).addTo(newMap).bindPopup("<h4>80-q-607-pa</h4>");
    setMarker(newMarker);

    // Set max zoom level
    newMap.setMinZoom(3);

    // Cleanup function to remove marker when component unmounts
    return () => {
      if (newMarker) {
        newMap.removeLayer(newMarker);
      }
    };
    
  }, [sock]);

  return <div id="map-container" style={{ width: "100%", height: "100vh" }}></div>;
}
