import Leaf from 'leaflet';
import React, { useEffect, useState } from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';

const WebSocketMap = () => {
  const [position, setPosition] = useState([51.505, -0.09]); // Default position
  const [ws, setWs] = useState(null);

  const customIcon = new Leaf.Icon({
    // iconUrl:  "https://freepngimg.com/thumb/car/75694-bird's-eye-car-top-view,plan-view-icon.png",
    iconUrl:require('./car.png'),
    iconSize: [35, 35],
    iconAnchor: [12, 41],
    popupAnchor: [1, -34],
    tooltipAnchor: [16, -28],
  });

  useEffect(() => {
    // Connect to WebSocket
    const socket = new WebSocket('ws://your-backend-url');

    // WebSocket event listeners
    socket.onopen = () => {
      console.log('WebSocket connected');
    };

    socket.onmessage = (event) => {
      const data = JSON.parse(event.data);
      setPosition([data.latitude, data.longitude]);
    };

    socket.onclose = () => {
      console.log('WebSocket disconnected');
    };

    // Save WebSocket connection to state
    setWs(socket);

    // Cleanup WebSocket connection on unmount
    return () => {
      socket.close();
    };
  }, []);

  return (
    <MapContainer center={position} zoom={13} style={{ height: '400px', width: '100%' }}>
      <TileLayer
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
      />
      <Marker position={position} >
        <Popup>Your location</Popup>
      </Marker>
    </MapContainer>
  );
};

export default WebSocketMap;
