import React, { useState } from 'react';
import { YMaps, Map, Placemark } from 'react-yandex-maps';

const Google = () => {
  const [markers, setMarkers] = useState([]);

  const handleAddMarker = () => {
    // Add a new marker to the map
    const newMarkers = [...markers, { id: markers.length, coordinates: [Math.random() * 180 - 90, Math.random() * 360 - 180] }];
    setMarkers(newMarkers);
  };

  const handleRemoveMarker = (idToRemove) => {
    // Remove the marker with the specified ID
    const updatedMarkers = markers.filter(marker => marker.id !== idToRemove);
    setMarkers(updatedMarkers);
  };

  return (
    <div>
      <YMaps>
        <Map
          defaultState={{ center: [55.751244, 37.618423], zoom: 10 }}
          style={{ width: '100%', height: '400px' }}
        >
          {/* Render markers */}
          {markers.map(marker => (
            <Placemark
              key={marker.id}
              geometry={marker.coordinates}
              options={{ draggable: true }}
              onDragEnd={(e) => console.log('New coordinates:', e.get('target').geometry.getCoordinates())}
              onBalloonOpen={() => console.log('Marker balloon opened')}
            />
          ))}
        </Map>
      </YMaps>
      <button onClick={handleAddMarker}>Add Marker</button>
      {/* Button to remove the first marker */}
      {markers.length > 0 && <button onClick={() => handleRemoveMarker(markers[0].id)}>Remove Marker</button>}
    </div>
  );
};

export default Google;
