import React, { useEffect } from 'react';
import { Map, Marker } from '@2gis/maps';

const Test = () => {
  useEffect(() => {
    const map = new Map('map', {
      center: [55.753215, 37.622504], // Moscow coordinates
      zoom: 10,
    });

    const marker = new Marker(map, {
      position: [55.753215, 37.622504],
      draggable: true,
    });

    return () => {
      // Cleanup
      marker.remove();
      map.destroy();
    };
  }, []);

  return (
    <div id="map" style={{ width: '100%', height: '500px' }}></div>
  );
};

export default Test;
