import React, { useEffect } from "react";

const MapComponent = () => {
  useEffect(() => {
    // Initialize the map
    const map = new DG.Map("map-container", {
      center: [55.751244, 37.618423], // Moscow coordinates
      zoom: 10 // Initial zoom level
    });

    // Add marker to the map
    const marker = DG.marker([55.751244, 37.618423]).addTo(map).bindPopup("Moscow");

    return () => {
      // Cleanup function to remove marker when component unmounts
      map.removeLayer(marker);
    };
  }, []); // Empty dependency array ensures this effect runs only once after initial render

  return <div id="map-container" style={{ width: "100%", height: "400px" }}></div>;
};

export default MapComponent;
