import React, { useState, useEffect } from 'react';
import { MapContainer, TileLayer, Marker, Popup, Polyline, Tooltip } from 'react-leaflet';

const MapWithWebSocket = () => {
  const [position, setPosition] = useState([51.505, -0.09]); // Initial position
  const [trail, setTrail] = useState([]);
  const [angles, setAngles] = useState([]);
  const [ws, setWs] = useState(null);

  useEffect(() => {
    // Establish WebSocket connection
    const socket = new WebSocket('ws://your-backend-url');
    setWs(socket);

    // Cleanup on unmount
    return () => {
      socket.close();
    };
  }, []);

  useEffect(() => {
    if (!ws) return;

    // Listen for messages from the WebSocket server
    ws.onmessage = (event) => {
      const newData = JSON.parse(event.data);

      // Update position
      setPosition([newData.latitude, newData.longitude]);

      // Update trail
      setTrail((prevTrail) => [...prevTrail, [newData.latitude, newData.longitude]]);

      // Calculate angles between consecutive segments in the trail
      if (trail.length >= 2) {
        const newAngles = [];
        for (let i = 1; i < trail.length - 1; i++) {
          const angle = calculateAngle(trail[i - 1], trail[i], trail[i + 1]);
          newAngles.push(angle);
        }
        setAngles(newAngles);
      }
    };

    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [ws]);

  // Calculate angle between three points
  const calculateAngle = (p1, p2, p3) => {
    const angle1 = Math.atan2(p2[1] - p1[1], p2[0] - p1[0]);
    const angle2 = Math.atan2(p3[1] - p2[1], p3[0] - p2[0]);
    let angle = (angle2 - angle1) * (180 / Math.PI);
    if (angle < 0) angle += 360;
    return angle.toFixed(2);
  };

  return (
    <MapContainer center={position} zoom={13} style={{ height: '400px', width: '100%' }}>
      <TileLayer
        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
      />
      <Marker position={position} icon={requ}>
        <Popup>You are here</Popup>
      </Marker>
      <Polyline pathOptions={{ color: 'blue' }} positions={trail} />
      {angles.map((angle, index) => (
        <Marker key={index} position={trail[index + 1]}>
          <Tooltip direction="bottom" offset={[0, 20]}>{angle}Â°</Tooltip>
        </Marker>
      ))}
    </MapContainer>
  );
};

export default MapWithWebSocket;
